import React, { Component } from 'react';

import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import {BrowserRouter, Route, Link, Switch} from 'react-router-dom'

import HeaderName from './component/HeaderName'
import HeaderNav from './component/HeaderNav'
import HomePage from './component/Home'
import Albums from './component/Albums'
import Album from './component/Album'
import ResumePage from './component/Resume'
import Footer from './component/Foot'

import './App.css';


class App extends Component {

  render(){
    return(
      <Container fluid style={{background:"white"}}>
        <BrowserRouter>
        <Row style={{height:"15vh"}}>
          <Col style={{borderColor:"black"}} xs={4}>
            <HeaderName/>
          </Col>
          <Col style={{borderColor:"black",justifyContent:"center",alignItems:"center"}} xs={8}>
            <HeaderNav />
          </Col>
        </Row>

        <Switch>

          <Route path="/home/" component={HomePage} />
          <Route path="/short_films/" render={() => (<Albums type="short_films" />)} />
          <Route path="/short_film/:id" render={(_match) => (<Album type="short_films" match={_match} />)} />
          <Route path="/resume/" component={ResumePage} />
          <Route path="/" component={HomePage} />
        </Switch>

        <Row style={{height:"5vh", bottom:0, textAlign:"center", justifyContent:"center"}}>
          <Footer />
        </Row>
        </BrowserRouter>
      </Container>
    )
  }
};

export default App;
