import React, { Component } from 'react';

import {Card, CardColumns, Col, Row} from 'react-bootstrap'

import RealCard from './RealCard'

class ShortFilm extends Component {
    constructor(props){
        super(props);

        this.state={
            data:null,
            id:null
        }
    }

    componentDidMount(){
        var _id = this.props.match.params.id
        this.setState({id:_id})
        let mycomp = this;
        fetch('/data/data.json').then(
            function(response){
                return response.json()
            }
        ).then(
            function(responseData){
                // console.log(responseData)
                mycomp.setState({data:responseData.short_films})
            }
        ).catch(
            function(error){
                console.log(error)
            }
        )
    }

    render(){
        var items,i,sf;
        items=[];
        sf = {
            "name":"Unknown"
        }
        if(this.state.data != null){
            for(i=0;i<this.state.data.length;i++){
                if(this.state.data[i].id === this.state.id){
                    sf = this.state.data[i]
                    var j;
                    for (j=0;j<sf.pics.length;j++){
                        items.push(
                            <RealCard
                            key={sf.pics[j].src}
                            src={sf.pics[j].src}
                            text={sf.pics[j].src}
                            piconly={true}
                            />
                        )
                    }
                }
            }
        }

        return (
            <Row style={{minHeight:"80vh",justifyContent:"center",alignItems:"center"}}>
            <Col style={{borderColor:"white"}} xs={8}>
                <h1 style={{color:"white"}}>{sf.name}</h1>
                {items}
            </Col>
            </Row>
        );
    }
};

export default ShortFilm;
