import React, { Component } from 'react';
// import Dimensions from 'react-dimensions';

import {Card, CardColumns, Col, Row} from 'react-bootstrap'

class Album extends Component {
    constructor(props){
        super(props);

        this.state={
            data:null,
            id:null,
            type:null,
            generalwidth:null
        }

        this.myInput=React.createRef()
    }

    getdata(){
        let mycomp = this;
        fetch('/data/data.json').then(
            function(response){
                return response.json()
            }
        ).then(
            function(responseData){
                // console.log(responseData)
                mycomp.setState({data:responseData[mycomp.state.type]})
            }
        ).catch(
            function(error){
                console.log(error)
            }
        )
    }

    componentWillReceiveProps(nextProps){
        if(this.props.type !== nextProps.type){
            console.log(this.props.type)
            this.setState({type:nextProps.type})
            this.getdata()
        }
    }

    componentDidMount(){
        var _id = this.props.match.params.id
        this.setState({id:_id, type:this.props.type})
        this.getdata()
        console.log(window.innerWidth, window.innerHeight, this.myInput.current.offsetWidth)
        this.setState({generalwidth:this.myInput.current.offsetWidth})
    }

    render(){
        var items,i,sf,namefont;
        items=[];
        sf = {
            "name":"Unknown"
        }
        console.log(this.state.data)
        if(this.state.data !== null){
            namefont=this.state.data.font.name
            for(i=0;i<this.state.data.content.length;i++){
                console.log(this.state.data.content[i].id,parseInt(this.state.id,10))
                if(this.state.data.content[i].id === parseInt(this.state.id,10)){
                    sf = this.state.data.content[i]
                    var j;
                    for (j=0;j<sf.pics.length;j++){
                        var imgitem
                        if(this.state.generalwidth === null){
                            imgitem = (<img src={sf.pics[j].src} style={{width:"100%"}} />)
                        }else{
                            imgitem = (<img src={sf.pics[j].src} style={{width:700, height:700}} />)
                        }
                        items.push(
                            <div key={sf.pics[j].src} style={{width:"100%"}}>
                                {imgitem}
                                <p style={{textAlign:"center", fontFamily:sf.font.text}}>{sf.pics[j].text}</p>
                                <br />
                            </div>
                        )
                    }
                    break;
                }
            }
        }else{
            namefont = ""
        }

        console.log(items)

        return (
            <Row style={{justifyContent:"center",alignItems:"center"}}>
                <Col ref={this.myInput} style={{borderColor:"black"}} xs={10}>
                    <h1 style={{color:"black", textAlign:"center", fontFamily:namefont}}>{sf.name}</h1>
                    {items}
                </Col>
            </Row>
        );
    }
};

export default Album;
