import React, { Component } from 'react';
import Carousel from 'react-bootstrap/Carousel'

import Pic1 from '../img/NY17.jpeg'
import Pic2 from '../img/NY18.jpeg'
import Pic3 from '../img/NY19.jpeg'

import RealCarousel from './RealCarousel'

class CarouselPage extends Component {
    constructor(props){
        super(props);

        this.state={
            data:null
        }
    }

    componentDidMount(){
        fetch('/data/data.json').then(
            function(response){
                return response.json()
            }
        ).then(
            function(responseData){
                console.log(responseData)
                this.setState({data:responseData})
            }
        ).catch(
            function(error){
                console.log(error)
            }
        )
    }

    render(){

        var items,i;
        items=[];
        for(i=0;i<this.state.data.homepage.length;i++){
            console.log(this.state.data.homepage[i])
            items.push(
                <Carousel.Item>
                    <img
                    className="d-block w-100"
                    src={this.state.data.homepage[i].src}
                    alt={this.state.data.homepage[i].src}
                    style={{maxHeight:"80vh"}}
                    />
                    <Carousel.Caption>
                    <h3>{this.state.data.homepage[i].title}</h3>
                    <p>{this.state.data.homepage[i].text}</p>
                    </Carousel.Caption>
                </Carousel.Item>
            )
        }   

        return (
            <Carousel style={{height:"100%"}}>
                {items}
            </Carousel>
        );
    }
};

export default CarouselPage;
