import React, { Component } from 'react';

import {Card, CardColumns, Col, Row} from 'react-bootstrap'

import RealCard from './RealCard'

class ShortFilms extends Component {
    constructor(props){
        super(props);

        this.state={
            data:null
        }
    }

    componentDidMount(){
        let mycomp = this;
        fetch('/data/data.json').then(
            function(response){
                return response.json()
            }
        ).then(
            function(responseData){
                // console.log(responseData)
                mycomp.setState({data:responseData})
            }
        ).catch(
            function(error){
                console.log(error)
            }
        )
    }

    render(){
        var items,i;
        items=[];
        console.log(this.state.data)
        if(this.state.data != null){
            for(i=0;i<this.state.data.short_films.length;i++){
                console.log(this.state.data.homepage[i])
                items.push(
                    <RealCard
                    key={this.state.data.short_films[i].id}
                    id={this.state.data.short_films[i].id}
                    src={this.state.data.short_films[i].src}
                    alt={this.state.data.short_films[i].src}
                    title={this.state.data.short_films[i].title}
                    text={this.state.data.short_films[i].text}
                    />
                )
            }
        } 
        return (
            <Row style={{minHeight:"80vh",justifyContent:"center",alignItems:"center"}}>
            <Col style={{borderColor:"white"}} xs={8}>
            <CardColumns>
            {items}
            </CardColumns>
            </Col>
            </Row>
        );
    }
};

export default ShortFilms;
