import React, { Component } from 'react';
import Carousel from 'react-bootstrap/Carousel'

class CarouselPage extends Component {
    constructor(props){
        super(props);

        this.state={
            data:null
        }
    }

    componentDidMount(){
        let mycomp = this;
        fetch('/data/data.json').then(
            function(response){
                return response.json()
            }
        ).then(
            function(responseData){
                // console.log(responseData)
                mycomp.setState({data:responseData.homepage})
            }
        ).catch(
            function(error){
                console.log(error)
            }
        )
    }

    render(){

        var items,i;
        items=[];
        console.log(this.state.data)
        if(this.state.data != null){
            for(i=0;i<this.state.data.content.length;i++){
                // console.log(this.state.data.homepage[i])
                // calculate img height & width

                items.push(
                    <Carousel.Item
                    key={this.state.data.content[i].src}
                    >
                        <img
                        className="d-block w-100"
                        src={this.state.data.content[i].src}
                        alt={this.state.data.content[i].src}
                        // style={{height:realheight, width:realwidth}}
                        />
                        <Carousel.Caption>
                        <h3 style={{fontFamily:this.state.data.font.title}}>{this.state.data.content[i].title}</h3>
                        <p style={{fontFamily:this.state.data.font.text}}>{this.state.data.content[i].text}</p>
                        </Carousel.Caption>
                    </Carousel.Item>
                )
            }
        } 

        return (
            <Carousel style={{height:"100%"}}>
                {items}
            </Carousel>
        );
    }
};

export default CarouselPage;
