import React, { Component } from 'react';

import {Image} from 'react-bootstrap'

class HeaderName extends Component {
    constructor(props){
        super(props);
        this.state={
            data:{
                logo:"",
                name:"",
                title:"",
                font:{
                    name:"",
                    title:""
                }
            }
        }
    }

    componentWillMount(){
        this.getdata()
    }

    componentDidMount(){
        console.log("Getting Data")
        this.getdata()
    }

    getdata(){
        console.log("Getting Data")
        let mycomp = this;
        fetch('/data/data.json').then(
            function(response){
                return response.json()
            }
        ).then(
            function(responseData){
                console.log(responseData)
                mycomp.setState({data:responseData["header"]})
            }
        ).catch(
            function(error){
                console.log(error)
            }
        )
    }

    render(){
        // console.log(this.state.data)
        return (
            <div style={{display:"inline-flex", flexDirection:"row", color:'black', marginLeft:"8.5vw", marginTop:"4.5vh"}}>
                <div>
                    <div style={{background:"white",display:"inline-flex", flexDirection:"row", color:'black'}}>
                        <Image src={this.state.data.logo} style={{marginTop:"0.9vh", width:"8%",height:"8%"}} rounded />
                        <h1 style={{marginLeft:"1vw", fontFamily:this.state.data.font.name, marginBottom:"0vh", fontSize:"5vh"}}> {this.state.data.name}</h1>
                    </div>
                    <h5 style={{fontFamily:this.state.data.font.title}}>{this.state.data.title}</h5>
                </div>
            </div>
        );
    }
};

export default HeaderName;
