import React, { Component } from 'react';

import {Image} from 'react-bootstrap'

class HeaderName extends Component {
    constructor(props){
        super(props);
        this.state={
            data:{
                logo:"",
                name:"",
                title:"",
            }
        }
    }

    componentWillMount(){
        this.getdata()
    }

    componentDidMount(){
        console.log("Getting Data")
        this.getdata()
    }

    getdata(){
        console.log("Getting Data")
        let mycomp = this;
        fetch('/data/data.json').then(
            function(response){
                return response.json()
            }
        ).then(
            function(responseData){
                console.log(responseData)
                mycomp.setState({data:responseData["header"]})
            }
        ).catch(
            function(error){
                console.log(error)
            }
        )
    }

    render(){
        // console.log(this.state.data)
        return (
            <div style={{fontFamily:"Hammersmith One", display:"inline-flex", flexDirection:"row", color:'black', marginLeft:"16vh", marginTop:"4vh"}}>
                <div>
                    <Image src={this.state.data.logo} thumbnail />
                </div>
                <div>
                    <h1>{this.state.data.name}</h1>
                    <h3>{this.state.data.title}</h3>
                </div>
            </div>
        );
    }
};

export default HeaderName;
