import React, { Component } from 'react';

import {Card, CardColumns, Col, Row} from 'react-bootstrap'

class Album extends Component {
    constructor(props){
        super(props);

        this.state={
            data:null,
            id:null,
            type:null
        }
    }

    getdata(){
        let mycomp = this;
        fetch('/data/data.json').then(
            function(response){
                return response.json()
            }
        ).then(
            function(responseData){
                // console.log(responseData)
                mycomp.setState({data:responseData[mycomp.state.type]})
            }
        ).catch(
            function(error){
                console.log(error)
            }
        )
    }

    componentWillReceiveProps(nextProps){
        if(this.props.type !== nextProps.type){
            console.log(this.props.type)
            this.setState({type:nextProps.type})
            this.getdata()
        }
    }

    componentDidMount(){
        console.log(this.props)
        var _id = this.props.match.params.id
        this.setState({id:_id, type:this.props.type})
        this.getdata()
    }

    render(){
        var items,i,sf,namefont;
        items=[];
        sf = {
            "name":"Unknown"
        }
        console.log(this.state.data)
        if(this.state.data !== null){
            namefont=this.state.data.font.name
            for(i=0;i<this.state.data.content.length;i++){
                console.log(this.state.data.content[i].id,parseInt(this.state.id,10))
                if(this.state.data.content[i].id === parseInt(this.state.id,10)){
                    sf = this.state.data.content[i]
                    var j;
                    for (j=0;j<sf.pics.length;j++){
                        console.log(sf.pics[j])
                        items.push(
                            <div key={sf.pics[j].src}>
                                <Card border="white" className="bg-white text-black">
                                    <Card.Img src={sf.pics[j].src}/>
                                    <p style={{textAlign:"center", fontFamily:sf.font.text}}>{sf.pics[j].text}</p>
                                </Card>
                                <br />
                            </div>
                        )
                    }
                    break;
                }
            }
        }else{
            namefont = ""
        }

        console.log(items)

        return (
            <Row style={{minHeight:"80vh",justifyContent:"center",alignItems:"center"}}>
            <Col style={{borderColor:"black"}} xs={10}>
                <h1 style={{color:"black", textAlign:"center", fontFamily:namefont}}>{sf.name}</h1>
                {items}
            </Col>
            </Row>
        );
    }
};

export default Album;
