import React, { Component } from 'react';
import Carousel from 'react-bootstrap/Carousel'

import Pic1 from '../img/NY17.jpeg'
import Pic2 from '../img/NY18.jpeg'
import Pic3 from '../img/NY19.jpeg'

import RealCarousel from './RealCarousel'

class CarouselPage extends Component {
    constructor(props){
        super(props);
        let _data = fetch('/data/data.json')
        console.log(_data)
        state={
            data:_data
        }
    }

    componentDidMount(){
        var i;
        for(i=0;i<this.state.data.homepage.length;i++){
            this.setState(this.state.concat(
                console.log(this.state.data.homepage[i])
                [this.state.data.homepage[i]]
            ))
        }   
    }

    render(){

        return (
            <Carousel style={{height:"100%"}}>
                {
                    this.state.map(
                        (item) => (
                            <Carousel.Item>
                                <img
                                className="d-block w-100"
                                src={item.src}
                                alt={item.src}
                                style={{maxHeight:"80vh"}}
                                />
                                <Carousel.Caption>
                                <h3>{item.title}</h3>
                                <p>{item.text}</p>
                                </Carousel.Caption>
                            </Carousel.Item>
                        )
                    )
                }
                <Carousel.Item>
                    <img
                    className="d-block w-100"
                    src={Pic1}
                    alt="Pic1"
                    style={{maxHeight:"80vh"}}
                    />
                    <Carousel.Caption>
                    <h3>Pic1</h3>
                    <p>Pic1</p>
                    </Carousel.Caption>
                </Carousel.Item>
                <Carousel.Item>
                    <img
                    className="d-block w-100"
                    src="/img/NY18.jpeg"
                    alt="Pic1"
                    style={{maxHeight:"80vh"}}
                    />
                    <Carousel.Caption>
                    <h3>Pic1</h3>
                    <p>Pic1</p>
                    </Carousel.Caption>
                </Carousel.Item>
            </Carousel>
        );
    }
};

export default CarouselPage;
