import React, { Component } from 'react';

import {Card, CardColumns, Col, Row} from 'react-bootstrap'

import RealCard from './RealCard'

class Albums extends Component {
    constructor(props){
        super(props);

        this.state={
            data:null,
            type:null
        }
    }

    componentWillReceiveProps(nextProps){
        if(this.props.type !== nextProps.type){
            console.log(this.props.type)
            this.setState({type:nextProps.type})
        }
    }

    componentDidMount(){
        console.log(this.props.type)
        let mycomp = this;
        fetch('/data/data.json').then(
            function(response){
                return response.json()
            }
        ).then(
            function(responseData){
                // console.log(responseData)
                mycomp.setState({data:responseData[mycomp.props.type]})
            }
        ).catch(
            function(error){
                console.log(error)
            }
        )
    }

    render(){
        var items,i;
        items=[];
        if(this.state.data != null){
            for(i=0;i<this.state.data.length;i++){
                // console.log(this.state.data.homepage[i])
                var _url="/"+this.props.type.substring(0,this.props.type.length-1)+"/"+this.state.data[i].id
                items.push(
                    <RealCard
                    key={this.state.data[i].id}
                    id={this.state.data[i].id}
                    src={this.state.data[i].src}
                    alt={this.state.data[i].src}
                    title={this.state.data[i].title}
                    text={this.state.data[i].text}
                    url={_url}
                    />
                )
            }
        } 
        return (
            <Row style={{minHeight:"80vh",justifyContent:"center",alignItems:"center"}}>
            <Col style={{borderColor:"black"}} xs={10}>
            <CardColumns>
            {items}
            </CardColumns>
            </Col>
            </Row>
        );
    }
};

export default Albums;
