import React, { Component, useCallback } from 'react';
// import Dimensions from 'react-dimensions';

import {Card, CardColumns, CardDeck, Col, Row} from 'react-bootstrap';
import Gallery from "react-photo-gallery";

import Carousel, { Modal, ModalGateway } from 'react-images';

class Album extends Component {
    constructor(props){
        super(props);

        this.state={
            data:null,
            id:null,
            type:null,
            generalwidth:null,
            index:0,
            open:false,
        }

        this.myInput=React.createRef()
        // this.handleClick=this.handleClick.bind(this)
    }

    getdata(){
        let mycomp = this;
        fetch('/data/data.json').then(
            function(response){
                return response.json()
            }
        ).then(
            function(responseData){
                // console.log(responseData)
                mycomp.setState({data:responseData[mycomp.state.type]})
            }
        ).catch(
            function(error){
                console.log(error)
            }
        )
    }

    componentWillReceiveProps(nextProps){
        if(this.props.type !== nextProps.type){
            console.log(this.props.type)
            this.setState({type:nextProps.type})
            this.getdata()
        }
    }

    componentDidMount(){
        var _id = this.props.match.params.id
        this.setState({id:_id, type:this.props.type})
        this.getdata()
        console.log(window.innerWidth, window.innerHeight, this.myInput.current.offsetWidth)
        this.setState({generalwidth:this.myInput.current.offsetWidth})
    }

    handleClick(_index){
        console.log("test",_index)
    }

    closeLightbox(){
        this.setState({index:0, open:false})
    }

    render(){
        var items,i,sf,namefont;
        items=[];
        sf = {
            "name":"Unknown",
            "column":0
        }
        console.log(this.state.data)
        if(this.state.data !== null){

            for(i=0;i<this.state.data.content.length;i++){
                console.log(this.state.data.content[i].id,parseInt(this.state.id,10))
                if(this.state.data.content[i].id === parseInt(this.state.id,10)){
                    sf = this.state.data.content[i]
                    namefont=sf.font.name

                    var columnnum = sf.column

                    var j;
                    for (j=0;j<sf.pics.length;j++){
                        var imgitem
                        if(this.state.generalwidth === null){
                            imgitem = (<img key={j} value={j} onClick={() => function(j){console.log(j)}} src={sf.pics[j].src} style={{width:"100%"}} />)
                        }else{
                            imgitem = (<img key={j} value={j} onClick={() => function(j){console.log(j)}} src={sf.pics[j].src} style={{padding:"5px 5px 5px 5px",width:this.state.generalwidth/columnnum, height:this.state.generalwidth/columnnum/sf.pics[i].ratio}} />)
                        }
                        items.push(
                            imgitem
                        )
                    }
                    break;
                }
            }
        }else{
            namefont = ""
        }



        console.log(sf)
        console.log(items)



        return (
                <Row style={{justifyContent:"center",alignItems:"center"}}>
                    <Col ref={this.myInput} style={{borderColor:"black"}} xs={10}>
                        <CardDeck >
                            {items}
                        </CardDeck>
                        <ModalGateway>
                            {this.state.open ? (
                            <Modal onClose={this.closeLightbox.bind(this)}>
                                <Carousel
                                currentIndex={this.state.index}
                                views={items.map(x => ({
                                    ...x,
                                    srcset: x.srcSet,
                                    caption: x.title
                                }))}
                                />
                            </Modal>
                            ) : null}
                        </ModalGateway>
                    </Col>
                </Row>

        )


    }
};

export default Album;
