import React, { Component } from 'react';

import {Image} from 'react-bootstrap'

class HeaderName extends Component {
    constructor(props){
        super(props);
        this.state={
            data:null
        }
    }

    getdata(){
        let mycomp = this;
        fetch('/data/data.json').then(
            function(response){
                return response.json()
            }
        ).then(
            function(responseData){
                // console.log(responseData)
                mycomp.setState({data:responseData["header"]})
            }
        ).catch(
            function(error){
                console.log(error)
            }
        )
    }

    render(){
        console.log(this.state.data)
        return (
            <div style={{fontFamily:"Hammersmith One", color:'black', marginLeft:"16vh", marginTop:"4vh"}}>
                <div>
                    <Image src={this.data.logo} roundedCircle />
                </div>
                <div>
                    <h1>{this.data.name}</h1>
                    <h3>{this.data.title}</h3>
                </div>
            </div>
        );
    }
};

export default HeaderName;
