import React, { Component } from 'react';

import {Card} from 'react-bootstrap'
import {Link} from 'react-router-dom'

class RealCard extends Component {
    constructor(props){
        super(props);
    }

    render(){
        var link = "/short_film/"+this.props.id
        if(this.props.piconly===true){
            return (
                <Card className="bg-dark text-white">
                    <Card.Img src={this.props.src}/>
                    <Card.ImgOverlay>
                    </Card.ImgOverlay>
                    <Card.Text>
                        {this.props.text}
                    </Card.Text>
                </Card>
            );
        }else{
            return (
                <Link to={link} style={{color:"white"}}>
                    <Card className="bg-dark text-white">
                        <Card.Img src={this.props.src} alt={this.props.alt} />
                        <Card.ImgOverlay>
                            <Card.Title>{this.props.title}</Card.Title>
                            <Card.Text>
                            {this.props.text}
                            </Card.Text>
                        </Card.ImgOverlay>
                    </Card>
                </Link>
            );
        }
    }
};

export default RealCard;
