import React, { Component, useCallback } from 'react';
// import Dimensions from 'react-dimensions';
import Img from './Img'
import CarouselPage from './Carousel'
import {Card, Carousel, CardDeck, Col, Row} from 'react-bootstrap';
import Gallery from "react-photo-gallery";

// import Carousel, { Modal, ModalGateway } from 'react-images';

class Album extends Component {
    constructor(props){
        super(props);

        this.state={
            data:null,
            id:null,
            type:null,
            generalwidth:null,
            index:0,
            open:false,
        }

        this.myInput=React.createRef()
        this.handleClick=this.handleClick.bind(this)
        this.closeLightbox=this.closeLightbox.bind(this)
        this.openLightbox = this.openLightbox.bind(this)
    }

    getdata(){
        let mycomp = this;
        fetch('/data/data.json').then(
            function(response){
                return response.json()
            }
        ).then(
            function(responseData){
                console.log(responseData)
                console.log(mycomp.state.type)
                mycomp.setState({data:responseData[mycomp.state.type]})
            }
        ).catch(
            function(error){
                console.log(error)
            }
        )
    }

    componentWillReceiveProps(nextProps){
        if(this.props.type !== nextProps.type){
            var _id = nextProps.match.params.id
            // console.log(this.props.type,nextProps.type,_id)
            this.setState({id:_id, type:nextProps.type})
            this.getdata()
            this.setState({generalwidth:this.myInput.current.offsetWidth})
        }
    }

    componentDidMount(){
        var _id = this.props.match.params.id
        this.setState({id:_id, type:this.props.type})
        this.getdata()
        this.setState({generalwidth:this.myInput.current.offsetWidth})
    }

    handleClick(_index){
        // console.log("test",_index)
        this.setState({index:_index, open:true})
    }

    openLightbox(event, {photo, index}){
        // console.log(event, photo, index)
        this.setState({index:index, open:true})
    }

    closeLightbox(){
        this.setState({index:0, open:false})
    }

    render(){
        var items,i,sf,namefont;
        items=[];
        sf = {
            "name":"Unknown",
            "column":0,
        }
        // console.log(this.state.data)

        if(this.state.data !== null){

            for(i=0;i<this.state.data.content.length;i++){
                // console.log(this.state.data.content[i].id,parseInt(this.state.id,10))
                if(this.state.data.content[i].id === parseInt(this.state.id,10)){
                    sf = this.state.data.content[i]
                    namefont=sf.font.name

                    var columnnum = sf.column
                    if(columnnum === 0){
                        columnnum = 1
                    }

                    var j;


                    for (j=0;j<sf.pics.length;j++){
                        var imgitem
                        if(this.state.generalwidth === null){
                            // imgitem = {'src':sf.pics[j].src, 'index':j, 'width':1, height:this.state.generalwidth/sf.pics[j].ratio}
                            // imgitem = (<Img key={j} onClick={this.handleClick} index={j} src={sf.pics[j].src} style={{borderColor:"black",width:"100%"}} />)
                            // imgitem = {'src':sf.pics[j].src, 'width':1, 'height':sf.pics[j].ratio}
                        }else{
                            // console.log(j,window.innerWidth, window.innerHeight, this.myInput.current.offsetWidth, this.state.generalwidth/columnnum, this.state.generalwidth/columnnum/sf.pics[j].ratio)
                            if(sf.pics[j].large=="true"){
                                imgitem = {'src':sf.pics[j].src, 'index':j, 'width':this.state.generalwidth, height:this.state.generalwidth/sf.pics[j].ratio}
                                // imgitem = (<Img key={j} onClick={this.handleClick} index={j} src={sf.pics[j].src} style={{padding:"5px 5px 5px 5px",width:this.state.generalwidth, height:this.state.generalwidth/sf.pics[j].ratio}} />)
                            }else{
                                imgitem = {'src':sf.pics[j].src, 'index':j, 'width':this.state.generalwidth/columnnum, height:this.state.generalwidth/columnnum/sf.pics[j].ratio}
                                // imgitem = (<Img key={j} onClick={this.handleClick} index={j} src={sf.pics[j].src} style={{padding:"5px 5px 5px 5px",width:this.state.generalwidth/columnnum, height:this.state.generalwidth/columnnum/sf.pics[j].ratio}} />)
                            }
                            // imgitem = {'src':sf.pics[j].src, 'width':100*sf.pics[j].ratio, 'height':100}
                            items.push(
                                imgitem
                            )
                        }
                    }
                    break;
                }
            }
        }else{
            namefont = ""
        }
        // console.log(items)
        return (
                <Row style={{minHeight:"80vh", justifyContent:"center",alignItems:"center"}}>
                    <Col ref={this.myInput} style={{borderColor:"black"}} xs={10}>
                    {this.state.open ? (
                            <div>
                            <CarouselPage index={this.state.index} data={sf} />
                            <button onClick={this.closeLightbox}>back</button>
                            </div>

                        ) : (
                            sf.column === 0 ? <Gallery photos={items} onClick={this.openLightbox}/> : (
                                <CardDeck >
                                    {items.map(
                                        item => (
                                            <Img key={item.index} onClick={this.handleClick} index={item.index} src={item.src} style={{backgroundColor:"transparent",borderColor:"transparent",padding:"1px 1px 1px 1px",width:item.width, height:item.height}}/>
                                        )
                                    )}
                                </CardDeck>
                            )

                        )}
                    </Col>
                </Row>

        )


    }
};

export default Album;
